        ,-9999999 UnitCost
  FROM imp_KoreDetail t1 WITH (NoLock)
  WHERE NOT EXISTS (SELECT 1
                    FROM tbl_KoreCostPreUnit t101 WITH (NoLock)
                    WHERE t101.SenderType = t1.SenderType
                      AND t101.RecordType = t1.RecordType
                      AND @ldt_InvoiceKeyDate BETWEEN t101.InvoiceFromDate AND t101.InvoiceToDate)
  GROUP BY t1.SenderType
