        LEFT OUTER JOIN (SELECT t101.GSM GSM
                               ,t101.SenderType + ' ' + t101.RecordType UsageClass
                               ,Sum(CASE WHEN t101.RecordType = 'GPRS' THEN t101.TotalVolumeDownLink ELSE 1 END) Quantity
                         FROM imp_KoreDetail t101 WITH (NoLock)
                         GROUP BY t101.GSM
                                 ,t101.SenderType + ' ' + t101.RecordType) x2
          ON x2.GSM = x1.GSM
            AND x2.UsageClass = x1.UsageClass
      WHERE IsNull(x2.Quantity, -1) <> x1.SumQuantity

      DECLARE @tbl_error TABLE (GSM VARCHAR(50) NOT NULL, PRIMARY KEY (GSM))

      INSERT INTO @tbl_error (GSM)
      SELECT x1.GSM
      FROM (SELECT t101.GSM
                  ,t101.UsageClass
