          ON x2.GSM = x1.GSM
            AND x2.UsageClass = x1.UsageClass
      WHERE IsNull(x2.Quantity, -1) <> x1.SumQuantity
      GROUP BY x1.GSM

      SELECT TOP 1000 'imp_KoreSummary', *
      FROM imp_KoreSummary t1 WITH (NoLock)
        INNER JOIN @tbl_error t2
          ON t2.GSM = t1.GSM

      SELECT TOP 1000 'imp_KoreSummary2', *
      FROM imp_KoreSummary2 t1 WITH (NoLock)
        INNER JOIN @tbl_error t2
          ON t2.GSM = t1.GSM

      SELECT TOP 10000 'imp_KoreDetail', *
      FROM imp_KoreDetail t1 WITH (NoLock)
        INNER JOIN @tbl_error t2
